PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer not null primary key autoincrement, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer not null primary key autoincrement, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "default_team" integer, view integer, show_ticket_count integer);
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer not null primary key autoincrement, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime default CURRENT_TIMESTAMP not null);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer not null primary key autoincrement, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "b_s" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "name" varchar not null);
CREATE TABLE IF NOT EXISTS "c_i_s" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "bs_id" integer not null, "vendor" varchar);
CREATE TABLE IF NOT EXISTS "teams" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "name" varchar not null, "description" varchar, "email" varchar, "active" integer not null default '1');
CREATE TABLE IF NOT EXISTS "tickets" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "opened" datetime not null, "requester" integer not null, "category" varchar, "subcategory" varchar, "businessservice_id" integer, "configurationitem_id" integer, "channel" varchar, "status" varchar, "impact" varchar, "urgency" varchar, "priority" varchar, "owner_group" integer, "assignment_group" integer, "assigned_to" integer, "short_description" varchar, "description" varchar, "active" integer not null default '0', onhold_reason null);
CREATE TABLE IF NOT EXISTS "team_user" ("team_id" integer not null, "user_id" integer not null);
CREATE TABLE IF NOT EXISTS "articles" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "user_id" integer not null, "title" varchar not null, "body" text not null);
CREATE TABLE IF NOT EXISTS "settings" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "user_id" integer not null, "setting" varchar not null, "value" varchar not null);
CREATE TABLE IF NOT EXISTS "worknotes" (id integer primary key not null, created_at datetime, updated_at datetime, ticket_id integer not null, user_id integer not null, internal integer not null, body varchar, type varchar, data varchar);
CREATE TABLE IF NOT EXISTS "views" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "public" integer not null default '0', "created_by" integer, "updated_by" integer, "columns" varchar not null, "sorted_by" varchar, "grouped_by" varchar, "name" varchar not null, "filter" varchar);
CREATE TABLE IF NOT EXISTS "notifications" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "user_id" integer not null, "title" varchar, "body" text not null, "read_at" datetime);
CREATE TABLE IF NOT EXISTS "statuses" ("id" integer not null primary key autoincrement, "created_at" datetime, "updated_at" datetime, "name" varchar not null);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE UNIQUE INDEX "articles_title_unique" on "articles" ("title");
CREATE UNIQUE INDEX "settings_user_id_setting_unique" on "settings" ("user_id", "setting");
COMMIT;
